import axios from "axios"
import { CREATE_MENTOR_REQUEST, CREATE_MENTOR_SUCCESS, CREATE_MENTOR_FAIL } from "../../action";


//Create Mentor
export const createMentor = (mentorData) => {
    return async dispatch => {
        try {
            dispatch({
                type: CREATE_MENTOR_REQUEST
            })
            const config = {
                headers: { 'Content-Type': 'application/json' }
            }
            const { data } = await axios.post("https://mentorstudentapi-vj.herokuapp.com/api/mentor",
                (mentorData),
                config)
            dispatch({
                type: CREATE_MENTOR_SUCCESS,
                payload: data
            })
        }
        catch (error) {
            dispatch({
                type: CREATE_MENTOR_FAIL,
                payload: error.response && error.response.data.message
            })

        }
    }

}

// Reading mentors list
export const fetchMentors = () => {
    return async dispatch => {
        try {
            dispatch({
                type: FETCH_MENTORS_REQUEST
            })
            const config = {
                headers: { 'Content-Type': 'application/json' }
            }
            const { data } = await axios.get("https://mentorstudentapi-vj.herokuapp.com/api/student/list",   
                config)
            dispatch({
                type: FETCH_MENTORS_SUCCESS,
                payload: data
            })
        }
        catch (error) {
            dispatch({
                type: FETCH_MENTORS_FAIL,
                payload: error.response && error.response.data.message
            })

        }
    }
}
