import React,{useState, useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import { fetchMentors, fetchMentorstudents } from '../../redux/action/mentor/mentorAction';
import styles from '../../myStyles.module.css'

function MentorList() {
    const[id, setId] = useState();

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchMentors())
    }, [dispatch])

    const mentorList = useSelector(state => state.mentor)
    const {mentors, mentorstudents} = mentorList
    console.log(mentors)

    const idHandler = (e) => {
       setId((e) => e.target.value)
       dispatch(fetchMentorstudents(id))
    }

    return (
        
        <div>
           
            <div className={styles.stumen}>           
                <label>Select Mentor</label>
                <select onChange = {(e) => idHandler(e)}>
                    <option>Select Mentor </option>
                    {mentors && mentors.map(mentor => {
                        return (
                            <option value={mentor && mentor._id} key={mentor._id}>
                                {mentor.name}
                            </option>
                        )
                    })}
                </select>
                </div>
              
        </div>
    )
}

export default MentorList
