import React,{useState, useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import { fetchMentors, fetchMentorstudents } from '../../redux/action/mentor/mentorAction';
import styles from '../../myStyles.module.css'

function MentorList() {
    const[id, setId] = useState();
    const[mentorName, setMentorName] = useState()
  

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchMentors())             
    }, [dispatch])

    
   
    const mentorList = useSelector(state => state.mentor)
    const {mentors} = mentorList
    console.log(mentors) 

    setMentorName(mentors)
    

    const SubmitHandler = () => {
        useEffect(() => {
            dispatch(fetchMentorstudents(id))
        }, [])
    }

   

    return (
        
        <div>      
            <form onSubmit = {SubmitHandler}>     
            <div className={styles.stumen}>           
                <label>Select Mentor</label>
                <select onChange = {(e) => setId(e.target.value)}>
                    <option>Select Mentor </option>
                    {mentorName && mentorName.map(mentor => {
                        return (
                            <option value={mentor && mentor._id} key={mentor._id}>
                                {mentor.name}
                            </option>
                        )
                    })}
                </select>
                </div>
                <input type = "submit"/>
            </form>  
        </div>
    )
}

export default MentorList
